@Service
public class $!{tableName} {

    @Autowired
    private MongodbUtil mongodbUtil;


    public $!{tableInfo.name} get($!pk.shortType $!pk.name) {
        return ($!{tableInfo.name}) mongodbUtil.findSortFirst($!{tableInfo.name}.class, ImmutableMap.of(IncIdDocument.ID, id));
    }

    public $!{tableInfo.name} save($!{tableInfo.name} $!tool.firstLowerCase($!{tableInfo.name})) {
        return ($!{tableInfo.name}) mongodbUtil.save($!tool.firstLowerCase($!{tableInfo.name}));
    }

    public List<$!{tableInfo.name}> saveBatch(List<$!{tableInfo.name}> list) {
        return (List<$!{tableInfo.name}>) mongodbUtil.save(list);
    }

    public List<$!{tableInfo.name}> listByEntity($!{tableInfo.name} $!tool.firstLowerCase($!{tableInfo.name})) {
