        return (List<$!{tableInfo.name}>) mongodbUtil.findDesignField($!{tableInfo.name}.class, map);
    }

    public Page<$!{tableInfo.name}> listPageByEntity(int page, int size, $!{tableInfo.name} $!tool.firstLowerCase($!{tableInfo.name})) {
