        return (Page<$!{tableInfo.name}>) mongodbUtil.findSortPageCondition($!{tableInfo.name}.class, new HashMap<>(), page, size);
    }

    public long update($!{tableInfo.name} $!tool.firstLowerCase($!{tableInfo.name})) {
        Map<String, Object> map = JSONObject.parseObject(JSON.toJSONString($!tool.firstLowerCase($!{tableInfo.name})));
        return mongodbUtil.updateFirst($!{tableInfo.name}.class, ImmutableMap.of(IncIdDocument.ID, $!tool.firstLowerCase($!{tableInfo.name}) .getId()), map);
    }

    public long updateBatch(List<$!{tableInfo.name}> list) {
        long count = 0;
        for ($!{tableInfo.name} $!tool.firstLowerCase($!{tableInfo.name}) : list) {
            Map<String, Object> map = JSONObject.parseObject(JSON.toJSONString($!tool.firstLowerCase($!{tableInfo.name})));
            count += mongodbUtil.updateFirst($!{tableInfo.name}.class, ImmutableMap.of(IncIdDocument.ID, $!tool.firstLowerCase($!{tableInfo.name}) .getId()), map);
        }
        return count;
    }

